import React, { useState } from 'react';
import ConfigPanel from '../features/config/ConfigPanel';
import ChatInterface from '../features/chat/ChatInterface';
import './App.css';

function App() {
  const [isConfigured, setIsConfigured] = useState(false);
  const [sessionId, setSessionId] = useState('');
  const [provider, setProvider] = useState('');
  const [model, setModel] = useState('');

  const handleConfigSuccess = (config) => {
    setIsConfigured(true);
    setSessionId(config.session_id);
    setProvider(config.provider);
    setModel(config.model);
  };

  const handleReset = () => {
    setIsConfigured(false);
    setSessionId('');
    setProvider('');
    setModel('');
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>
          <i className="fas fa-robot"></i> LLM Chatbot
        </h1>
        {isConfigured && (
          <div className="config-info">
            <span className="badge">{provider}</span>
            <span className="badge">{model}</span>
            <button onClick={handleReset} className="btn-reset">
              <i className="fas fa-cog"></i> Reconfigure
            </button>
          </div>
        )}
      </header>

      <main className="app-main">
        {!isConfigured ? (
          <ConfigPanel onConfigSuccess={handleConfigSuccess} />
        ) : (
          <ChatInterface 
            sessionId={sessionId} 
            provider={provider}
            model={model}
          />
        )}
      </main>
    </div>
  );
}

export default App;
