import React, { useState, useEffect, useRef } from 'react';
import './ChatInterface.css';

const API_URL = 'http://localhost:5000';

function ChatInterface({ sessionId, provider, model }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim() || loading) return;

    const userMessage = input.trim();
    setInput('');
    
    // Add user message to chat
    setMessages(prev => [...prev, { role: 'user', content: userMessage }]);
    setLoading(true);

    try {
      const response = await fetch(`${API_URL}/api/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: userMessage,
          session_id: sessionId
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to get response');
      }

      // Add assistant response to chat
      setMessages(prev => [...prev, { 
        role: 'assistant', 
        content: data.response 
      }]);
    } catch (err) {
      setMessages(prev => [...prev, { 
        role: 'error', 
        content: `Error: ${err.message}` 
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleClearHistory = async () => {
    if (!window.confirm('Clear all chat history?')) return;

    try {
      await fetch(`${API_URL}/api/clear`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ session_id: sessionId })
      });
      setMessages([]);
    } catch (err) {
      alert('Failed to clear history: ' + err.message);
    }
  };

  return (
    <div className="chat-interface">
      <div className="chat-header">
        <div className="chat-info">
          <i className="fas fa-comments"></i>
          <span>Chat Session</span>
        </div>
        <button onClick={handleClearHistory} className="btn-clear">
          <i className="fas fa-trash"></i> Clear History
        </button>
      </div>

      <div className="chat-messages">
        {messages.length === 0 ? (
          <div className="empty-state">
            <i className="fas fa-comment-dots"></i>
            <p>Start a conversation!</p>
            <small>Your messages will appear here</small>
          </div>
        ) : (
          messages.map((msg, index) => (
            <div key={index} className={`message message-${msg.role}`}>
              <div className="message-avatar">
                {msg.role === 'user' ? (
                  <i className="fas fa-user"></i>
                ) : msg.role === 'error' ? (
                  <i className="fas fa-exclamation-triangle"></i>
                ) : (
                  <i className="fas fa-robot"></i>
                )}
              </div>
              <div className="message-content">
                <div className="message-header">
                  {msg.role === 'user' ? 'You' : msg.role === 'error' ? 'Error' : 'Assistant'}
                </div>
                <div className="message-text">{msg.content}</div>
              </div>
            </div>
          ))
        )}
        {loading && (
          <div className="message message-assistant">
            <div className="message-avatar">
              <i className="fas fa-robot"></i>
            </div>
            <div className="message-content">
              <div className="message-header">Assistant</div>
              <div className="message-text typing">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={handleSend} className="chat-input-form">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          className="chat-input"
          disabled={loading}
        />
        <button 
          type="submit" 
          className="btn-send"
          disabled={loading || !input.trim()}
        >
          <i className="fas fa-paper-plane"></i>
        </button>
      </form>
    </div>
  );
}

export default ChatInterface;
