import React, { useState } from 'react';
import './ConfigPanel.css';

const API_URL = 'http://localhost:5000';

function ConfigPanel({ onConfigSuccess }) {
  const [provider, setProvider] = useState('openai');
  const [apiKey, setApiKey] = useState('');
  const [model, setModel] = useState('gpt-3.5-turbo');
  const [sessionId, setSessionId] = useState(`session-${Date.now()}`);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const modelOptions = {
    openai: [
      'gpt-3.5-turbo',
      'gpt-4',
      'gpt-4-turbo',
      'gpt-4o',
      'gpt-4o-mini'
    ],
    gemini: [
      'gemini-pro',
      'gemini-1.5-pro',
      'gemini-1.5-flash'
    ]
  };

  const handleProviderChange = (e) => {
    const newProvider = e.target.value;
    setProvider(newProvider);
    setModel(modelOptions[newProvider][0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await fetch(`${API_URL}/api/configure`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          provider,
          api_key: apiKey,
          model,
          session_id: sessionId
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Configuration failed');
      }

      onConfigSuccess(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="config-panel">
      <div className="config-card">
        <div className="config-header">
          <i className="fas fa-cog"></i>
          <h2>Configure Your LLM</h2>
        </div>
        
        <form onSubmit={handleSubmit} className="config-form">
          <div className="form-group">
            <label htmlFor="provider">
              <i className="fas fa-server"></i> Provider
            </label>
            <select 
              id="provider"
              value={provider} 
              onChange={handleProviderChange}
              className="form-control"
            >
              <option value="openai">OpenAI</option>
              <option value="gemini">Google Gemini</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="model">
              <i className="fas fa-brain"></i> Model
            </label>
            <select 
              id="model"
              value={model} 
              onChange={(e) => setModel(e.target.value)}
              className="form-control"
            >
              {modelOptions[provider].map(m => (
                <option key={m} value={m}>{m}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="apiKey">
              <i className="fas fa-key"></i> API Key
            </label>
            <input
              id="apiKey"
              type="password"
              value={apiKey}
              onChange={(e) => setApiKey(e.target.value)}
              placeholder="Enter your API key"
              className="form-control"
              required
            />
            <small className="form-help">
              {provider === 'openai' ? (
                <>Get your key from <a href="https://platform.openai.com/api-keys" target="_blank" rel="noopener noreferrer">OpenAI Platform</a></>
              ) : (
                <>Get your key from <a href="https://makersuite.google.com/app/apikey" target="_blank" rel="noopener noreferrer">Google AI Studio</a></>
              )}
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="sessionId">
              <i className="fas fa-fingerprint"></i> Session ID
            </label>
            <input
              id="sessionId"
              type="text"
              value={sessionId}
              onChange={(e) => setSessionId(e.target.value)}
              placeholder="Unique session identifier"
              className="form-control"
              required
            />
          </div>

          {error && (
            <div className="error-message">
              <i className="fas fa-exclamation-circle"></i> {error}
            </div>
          )}

          <button 
            type="submit" 
            className="btn-primary"
            disabled={loading}
          >
            {loading ? (
              <>
                <i className="fas fa-spinner fa-spin"></i> Connecting...
              </>
            ) : (
              <>
                <i className="fas fa-check"></i> Start Chatting
              </>
            )}
          </button>
        </form>
      </div>
    </div>
  );
}

export default ConfigPanel;
